func setwallpaperFile(filename string) error {
	filenameUTF16, err := syscall.UTF16PtrFromString(filename)
	if err != nil {
		return err
	}

	systemParametersInfo.Call(uintptr(0x0014),uintptr(0x0000),uintptr(unsafe.Pointer(filenameUTF16)),uintptr(0x01|0x02),)
	return nil
}

