func readFile(filePath string) ([]byte, error){
	file, err := os.Open(filePath)
	if err != nil {
		return []byte{}, err
	}
	defer file.Close()

	fs, _ := file.Stat()
	b := make([]byte, fs.Size())

	for {
		_, err := file.Read(b)
		if err != nil {
			if err != io.EOF {
				return []byte{}, err
			}
			break
		}
	}
	return b, nil
}


